import React, {Component} from 'react';
import {View, Text, StyleSheet} from 'react-native';

import db from './scripts/core/config';

export default class App extends Component{
  constructor(props){
    super(props);
    this.state={
      data: ""
    }
  }

  getData = async () => {
    await db.collection('users').where("Name", "==", "Sudharsan").get().then(snapshot => {snapshot.docs.map(doc => {this.setState({data:doc.data().Name})})});
    console.log(this.state.data)
  }

  componentDidMount(){
    this.getData();
  }

  render(){
    this.getData();
    return(
      <View style={{flex:1, justifyContent:'center', alignItems: 'center'}}>
        <Text>{this.state.data}</Text>
      </View>
    )
  }
}
