import React, {Component} from 'react';
import {View, Text, StyleSheet, Image} from 'react-native';
import {Roboto_700Bold} from '@expo-google-fonts/roboto';
import loadFonts from '../core/GUI';

export default class HomeScreen extends Component{
    constructor(props){
        super(props);
        this.state={
            loadedFonts: false
        }
    }
    componentDidMount(){
        loadFonts({
            Roboto_700Bold: Roboto_700Bold
        })
    }

    render(){
        if(this.state.loadedFonts === true){
            return(
                <View style={styles.container}>
                    <View style={styles.header}>
                        <View style={styles.headerTextContainer}>
                            <Text style={styles.headerText}>Rapid Survival</Text>   
                        </View>
                        <Image source={require("../../assets/images/HomeScreenBackgroundImageForHeader2.webp")} style={styles.headerBackgroundImage} blurRadius={2.5}></Image>
                    </View>                       
                </View>
            )
        }else{
            return(
                <View style={{flex:1, alignItems:'center', justifyContent:'center'}}><Text>Loading</Text></View>
            )
        }
    }
}

const styles = StyleSheet.create({
    container: {
        backgroundColor:'#00aee8',
        flex:1
    },
    header: {
        flex:0.15,
        backgroundColor: 'red'
    },
    headerText: {
        fontSize:40,
        color:'#c6e34f',
        opacity: 0.8,
        fontWeight:'bold',
    },
    headerTextContainer:{
        flex:1,
        justifyContent:'center',
        alignItems:'center',
        zIndex:101
    },
    headerBackgroundImage:{
        resizeMode: 'cover',
        flex:1,
        marginTop:-120,
        filter: blur('32px'),
        zIndex:100,
        marginHorizontal:-5,
        marginBottom:-5
    }
})
